document.addEventListener("DOMContentLoaded",()=>{gsap.registerPlugin(ScrollTrigger);document.querySelectorAll("[data-parallax-layers]").forEach(e=>{let r=gsap.timeline({scrollTrigger:{trigger:e,start:"0% 0%",end:"100% 0%",scrub:0}});[{layer:"1",yPercent:70},{layer:"2",yPercent:55},{layer:"3",yPercent:40},{layer:"4",yPercent:10}].forEach((t,a)=>{r.to(e.querySelectorAll(`[data-parallax-layer="${t.layer}"]`),{yPercent:t.yPercent,ease:"none"},0===a?void 0:"<")})})});const lenis=new Lenis;lenis.on("scroll",ScrollTrigger.update);gsap.ticker.add(e=>{lenis.raf(1e3*e)});gsap.ticker.lagSmoothing(0);document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("contact-form"),r=document.getElementById("contact-status");if(!e||!r)return;e.addEventListener("submit",async t=>{t.preventDefault(),r.hidden=!1,r.textContent="Sendingâ€¦";try{const t=new FormData(e),a=await fetch(e.action,{method:"POST",headers:{Accept:"application/json"},body:t});a.ok?(r.textContent="Thanks! Your message has been sent.",e.reset()):r.textContent="Oops! There was a problem sending your message."}catch(t){r.textContent="Network error. Please try again later."}})});
